mapper.select:
select count(1)
from tb_menu


        and id = #{id}


        and name = #{name}


        and path = #{path}


        and component = #{component}


        and icon = #{icon}


        and create_time = #{createTime}


        and update_time = #{updateTime}


        and order_num = #{orderNum}


        and parent_id = #{parentId}


        and is_hidden = #{isHidden}


        and type = #{type}
